                  }
                >
                  <i class="ict ict-selfdrive d-none d-sm-inline-block"></i>
                  <div>Ô tô tự lái</div>
                </Nav.Link>
              </Nav.Item>
              <Nav.Item className="flex-grow-1">
                <Nav.Link
                  eventKey="3"
                  onClick={() =>
                    setSearchCar((state) => ({ ...state, type: "driver" }))
                  }
                >
                  <i class="ict ict-withdriver d-none d-sm-inline-block"></i>
                  <div>Ô tô có tài xế</div>
                </Nav.Link>
              </Nav.Item>
            </Nav>
          </Col>
          <Col xxl={4} lg={6} sm={8} className="shadow-lg p-3 bg-dark--20">
            <Tab.Content>
              <Tab.Pane eventKey="1">
                <Form1
                  SearchCar={SearchCar}
                  handleSubmit={handleSubmit}
                  getLocalStart={getLocalStart}
                  getLocalEnd={getLocalEnd}
                  handleStartTime={handleStartTime}
                  handleStartDate={handleStartDate}
                  handleEndDate={handleEndDate}
                  handleEndTime={handleEndTime}
                />
              </Tab.Pane>
              <Tab.Pane eventKey="2">
                <Form1
                  SearchCar={SearchCar}
                  handleSubmit={handleSubmit}
                  getLocalStart={getLocalStart}
                  getLocalEnd={getLocalEnd}
                  handleStartTime={handleStartTime}
                  handleStartDate={handleStartDate}                  
                  handleEndDate={handleEndDate}
                  handleEndTime={handleEndTime}
                />
              </Tab.Pane>
              <Tab.Pane eventKey="3">
                <Form2
                  SearchCar={SearchCar}
                  handleSubmit={handleSubmit}
                  getLocalStart={getLocalStart}
                  getLocalEnd={getLocalEnd}
                  handleStartTime={handleStartTime}
                  handleStartDate={handleStartDate}                  
                  handleChangeTime={handleChangeTime}
                />
                {SearchCar.endLocal ? (
                  <DistanceMatrixService
                    options={{
                      destinations: [16.049688435149964, 108.2100661856943],
                      origins: [16.04363655117909, 108.21768061845475],
                      travelMode: "DRIVING",
                    }}
                    callback={(response) => {
                      if (response) {
                        console.log(response);
                      }
                    }}
                  />
                ) : null}
              </Tab.Pane>
            </Tab.Content>
          </Col>
        </Row>
      </Tab.Container>
    </Container>
  );
